import { Component, OnInit, Input } from '@angular/core';
import { Pedidos } from 'src/app/shared';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { FormsModule } from '@angular/forms';




@Component({
  selector: 'app-modal-pedido',
  templateUrl: './modal-pedido.component.html',
  styleUrls: ['./modal-pedido.component.css']
})
export class ModalPedidoComponent {


  @Input() pedido!: Pedidos;

  constructor(public activeModal: NgbActiveModal){}

  ngOnInit(): void {

  }

}
<div class="modal-header">
    <button type="button" class="close" aria-label="Fechar"
    (click)="activeModal.dismiss()">
    <span aria-hidden="true">&times;</span>
    </button>
    <h4 class="modal-title" id="modalLabel"> Cliente </h4>
   </div>
   <div class="modal-body text-center">
    <div>
    <p>Data: {{ pedido.data }} </p>
    <p>Produtos: {{ pedido.produtos }}</p>
    <p>Quantidade: {{ pedido.quantidade }}</p>
    <p>CPF: {{ pedido.cpf}} </p>
    </div>
   </div>
   <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark"
    (click)="activeModal.close()">Fechar</button>
   </div>
